def move_item_type_relations_to_visualizers(apps, schema_editor):
    VisualizerItemType = apps.get_model('selia_visualizers', 'VisualizerItemType')
    VisualizerModuleItemType = apps.get_model('selia_visualizers', 'VisualizerModuleItemType')

    for rel in VisualizerModuleItemType.objects.all():
        vis_item_type, _ = VisualizerItemType.objects.get_or_create(
            visualizer=rel.visualizer_module.visualizer,
            item_type=rel.item_type,
            defaults=dict(is_active=False))

        #Â Make default visualizer for item type if a version of the visualizer
        # was previously activated.
        if rel.is_active:
            vis_item_type.is_active = True
            vis_item_type.save()

        # Make the default version of the visualizer if the module was activated.
        rel.visualizer_module.is_active = rel.is_active
        rel.save()


